$(document).ready(function(){$("form").submit(function(n){var t={id:$("input[name=ID]").val(),index:$("input[name=Index]").val()};$.ajax({type:"post",url:$(this).attr("action"),data:t,dataType:"json",encode:!0}).done(function(n){console.log(n);n.success?$(".help-block").prepend('<div class="alert alert-success"><input type="checkbox" checked="true" /><a class="illust" href="'+n.pixivURL+'">Pixiv Page<\/a> <a href="'+n.url+'" class="thumbnail"><span class="title">'+n.title+'<\/span> - <span class="author">'+n.author+'<\/span><img src="'+n.url+'" style="height:150px;"><\/a><\/div>'):$(".help-block").prepend('<div class="alert alert-danger">'+n.error+"<\/div>")});n.preventDefault()});$("#gibebb").click(function(){var n=Array(),t,r,i;if($(".alert-success").each(function(){$("input:checkbox",this).is(":checked")&&n.push({img:$("img",this).attr("src"),pixiv:$(".illust",this).attr("href"),title:$(".title",this).text(),author:$(".author",this).text()})}),n.length>0){t=$("#bbcode");t.val("");for(i in n)t.val(t.val()+"[t]"+n[i].img+"[/t]");t.val(t.val()+"\n\nSource:");r=0;for(i in n)r++,t.val(t.val()+' [url="'+n[i].pixiv+'"]'+n[i].title+" - "+n[i].author+"[/url]")}})});